    , SYSDATE
)


    SELECT MNO_SEQ.CURRVAL FROM DUAL

mapper.select:
select upw
from member
where uuid = #{uuid}

SELECT
      *
FROM  MEMBER
WHERE 1=1
  AND UUID = #{uuid}
  AND UPW  = #{upw}

mapper.update:
